$(document).ready(function () {
    $(document).on('click', '.kebab', function () {
        var $this = $(this);

        //// $this.children('article.main_clk_btn').toggleClass('active');
        ////$this.children('p.cross').toggleClass('active');
        $this.children('ul.dropdown').toggleClass('activekebab');
        $this.each(function () {
            $('body').click(function () {
                //alert("fine");
                $("ul.dropdown").removeClass('activekebab');
            })
        });
    })

});


